import React, { Component } from 'react';
import './App.css';

class Input extends Component {
    state = {
        taskName: ''
    }
    handleName = (e) => {
        // update task in state
        let userInput = e.target.value;
        this.setState({
            taskName: userInput
        })
    }
    handleSubmitTask = (e) => {
        if (e.key == 'Enter') {
            // add task and delete 
            this.props.addTask(this.state.taskName)
        }
    }
    render() {
        return (
            <div>
              <input onChange={ this.handleName } onKeyDown={ this.handleSubmitTask } />
            </div>
        );
    }
}

class ToDoApp extends Component {
    render() {
        return (
            <Input />
        );
    }
}

export default ToDoApp;
