import React, { Component } from 'react';
import './App.css';

class Input extends Component {
    state = {
        taskName: ''
    }
    handleName = (e) => {
        // update task in state
        let userInput = e.target.value;
        this.setState({
            taskName: userInput
        })
    }
    handleSubmitTask = (e) => {
        // if user pressed enter, add task and clear input
        if (e.key === 'Enter') {
            this.props.addTask(this.state.taskName)
            this.setState({
                taskName: ''
            })
        }
    }
    render() {
        return (
            <div>
              <input value={this.state.taskName} onChange={ this.handleName } onKeyDown={ this.handleSubmitTask } />
            </div>
        );
    }
}

class TaskList extends Component {
    render() {
        const tasks = this.props.tasks.map((name, index) => {
            return <li key={ index }>{ name }</li>
        });
        return (
            <div>
          { tasks }
          </div>);
    }
}

class ToDoApp extends Component {
    state = {
        tasks: []
    }
    pushNewTask = (taskName) => {
        this.setState({
            tasks: [...this.state.tasks, taskName]
        })
    }
    render() {
        return (
            <div>
              <Input addTask={this.pushNewTask} /> 
              <TaskList tasks={this.state.tasks} /> 
            </div>
        );
    }
}

export default ToDoApp;
