import React, { Component } from 'react';
import './App.css';

class Input extends Component {
    state = {
        taskName: ''
    }
    handleName = (e) => {
        // update task in state
        let userInput = e.target.value;
        this.setState({
            taskName: userInput
        })
    }
    handleSubmitTask = (e) => {
        // if user pressed enter, add task and clear input
        if (e.key === 'Enter') {
            this.props.addTask(this.state.taskName)
            this.setState({
                taskName: ''
            })
        }
    }
    render() {
        return (
            <div>
              <input value={this.state.taskName} onChange={ this.handleName } onKeyDown={ this.handleSubmitTask } />
            </div>
        );
    }
}

class TaskList extends Component {
    deleteTask = (e) => {
        console.log(e.target.value);
    }
    render() {
        return (
            <div>
              {this.props.tasks.map((name, index) => {
                return <input type='checkbox' key={index} value={name} onClick={this.deleteTask} />
              })}
            </div>
        );
    }
}

class ToDoApp extends Component {
    state = {
        tasks: []
    }
    handleTaskDelete = (taskName) => {

    }
    pushNewTask = (taskName) => {
        this.setState({
            tasks: [...this.state.tasks, taskName]
        })
    }
    render() {
        return (
            <div>
              <Input addTask={this.pushNewTask} /> 
              <TaskList tasks={this.state.tasks} deleteTask={this.handleTaskDelete} /> 
            </div>
        );
    }
}

export default ToDoApp;
